export function updateScore(answers, remainingTime, timeToGuess) {
    let score = answers.slice(-1)[0].correct === true ? (2 - ((timeToGuess - remainingTime) / timeToGuess)) * 100 : 0
    answers.slice(-1)[0].score = score
    return answers
}


export function getTotalScore(answers) {
    let scores = answers.map(answer => answer.score)
    console.log(scores)
    let totalScore = scores.reduce((a, b) => a + b)
    return totalScore
}
