import React, { Component } from 'react';
import SpotifyWebApi from 'spotify-web-api-js';

// my components
import Blindtest from './Blindtest'

const spotifyApi = new SpotifyWebApi();

class BlindtestSession extends Component {
    constructor(props) {
        super(props);
        this.state = {
            score: 0,
            songplayed: {},
            blindtestLength: 10
        }
    }

    // manage music : play 10 songs for 30 sec each
    manageMusic = () => {
        this.interval = setInterval(() => {
            spotifyApi.skipToNext()
        }, 30000)
        spotifyApi.getMyCurrentPlaybackState()
            .then(response => {
                console.log(response)
                this.setState({ currentSong: response.item.name })
            })
        console.log(this.state.currentSong)

    }

    updateScore = () => {
        let score = this.state.score
        score += 1
        this.setState({
            score
        })
        spotifyApi.skipToNext()
    }

    componentDidMount() {
        this.manageMusic()
    }

    render() {
        return (
            <div className="blindtest-session">

                <div className="informations">
                    <p>Welcome to the blindtest </p>
                    <p>You currently have {this.state.score} points</p>
                    <p>Previously played songs</p>
                </div>
                <Blindtest
                    access_token={this.props.access_token}
                    updateScore={this.updateScore}
                    gameStarted={this.props.gameStarted}
                />
            </div>

        );
    }
}

export default BlindtestSession;
