import React, { Component } from 'react';

// content
import spotifyPlaylists from './SpotifyPlaylists'

class PlaylistPicker extends Component {
    constructor(props) {
        super(props);
        this.state = {
            playlistSelected: {},
            spotifyPlaylists
        }
    }

    handleChange = (event) => {
        let selectedOption = event.target
        let selectedName = selectedOption.options[selectedOption.selectedIndex].getAttribute('name')
        let selectedUri = selectedOption.value
        this.setState({
            playlistSelected: {
                name: selectedName,
                uri: selectedUri
            }
        })
        this.props.selectPlaylist(this.state.playlistSelected)
    }

    render() {
        return (
            <div>
                <p>Select a playlist from the list</p>
                <select name="playlistList" id="playlistList" onChange={this.handleChange}>
                    <option value="placeholder"> -- select an option -- </option>
                    {this.state.spotifyPlaylists.map((playlist, key) =>
                        <option key={key} name={playlist.name} value={playlist.uri}>{playlist.name}</option>
                    )}
                </select>
            </div>
        );
    }
}

export default PlaylistPicker;
