import React, { Component } from 'react';
import Button from '../UI-components/Button';
import Blindtest from './Blindtest'
// import SpotifyWebApi from 'spotify-web-api-js';

import '../../css/Blindtest.css';

// const spotifyApi = new SpotifyWebApi();



class BlindtestGame extends Component {
    constructor() {
        super()
        this.state = {
            nextSongTimer: 5
        }
    }

    startCounter = () => {
        let nextSongTimer = this.state.nextSongTimer
        let interval = setInterval(() => {
            nextSongTimer -= 1
            this.setState({ nextSongTimer })
            console.log(nextSongTimer)
            if (nextSongTimer === 0) {
                clearInterval(interval)
            }
        }, 1000)
    }

    render() {
        return (
            <div className="blindestGame">
                <div className="close">
                    <Button content="close" onClick={this.props.closeGame} />
                </div>
                <div className="informations">
                    <p>You currently have 0 points</p>
                    <p>Previously played songs</p>
                </div>
                <div className="game">
                    <p>Are you fucking ready ?</p>
                    <Button content="yes, go go go" onClick={this.startCounter} />
                    <p>You have {this.state.nextSongTimer} seconds to guess the song</p>
                    <Blindtest access_token={this.props.access_token} />
                </div>

            </div>
        );
    }
}

export default BlindtestGame;
