import React, { Component } from 'react';
import './App.css';
import { CSVDownload, CSVLink } from 'react-csv';
// import querystring from 'query-string';



let defaultTextColor = "red";
let defaultStyle = {
  color: defaultTextColor
}

// let fakeServerData = {
//   user: {
//     name: "Patrick",
//     playlists: [
//       {
//         name: 'my fav',
//         songs: [
//           { name: 'get up stand up', duration: 1345 },
//           { name: 'jammin', duration: 1564 },
//           { name: 'marcher dans le sable', duration: 392 }
//         ]
//       }
//     ]
//   }
// }

class PlaylistCounter extends Component {
  render() {
    return (
      <div style={{ ...defaultStyle, width: '40%', display: 'inline-block' }}>
        <h2 style={defaultStyle}>
          {this.props.playlists.length} playlists
          </h2>
      </div >
    );
  }
}

class HoursCounter extends Component {
  render() {
    let allSongs = this.props.playlists.reduce((songs, eachPlaylist) => {
      return songs.concat(eachPlaylist.songs)
    }, []);

    let totalDuration = allSongs.reduce((sum, eachSong) => {
      return sum + eachSong.duration;
    }, 0)
    return (
      <div style={{ ...defaultStyle, width: '40%', display: 'inline-block' }}>
        <h2 style={defaultStyle}>
          {Math.round(totalDuration / 60)} Hours
        </h2>
      </div >
    );
  }
}

class Filter extends Component {
  render() {
    return (
      <div style={defaultStyle}>
        <img src="" alt="" />
        <input type="text" onKeyUp=
          {e => this.props.onTextChange(e.target.value)} />
      </div>
    );
  }
}

class Playlist extends Component {
  render() {
    let playlist = this.props.playlist;
    return (
      <div style={{ ...defaultStyle, width: '25%', display: 'inline-block' }}>
        <img src={playlist.imageUrl} alt="" style={{ width: '160px' }} />
        <h3>{playlist.name}</h3>
        <ul>
          {playlist.songs.map(song =>
            <li>{song.name}</li>
          )}
        </ul>
      </div>

    );
  }
}

class DownloadPlaylist extends Component {
  render() {
    let playlists = this.props.playlists;
    console.log(playlists)
    let playlistsToDownload = this.props.playlists.map(playlist =>
      playlistsToDownload[0] = playlist.name
    )

    let csvData = [
      ['firstname', 'lastname', 'email'],
      ['Ahmed', 'Tomi', 'ah@smthing.co.com'],
      ['Raed', 'Labes', 'rl@smthing.co.com'],
      ['Yezzi', 'Min l3b', 'ymin@cocococo.com']
    ]

    return (

      < CSVLink data={csvData}
        filename={"my-file.csv"}
        target="_blank" >
        Download playlists as CSV
      </CSVLink >

    )
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      serverData: {},
      filterString: ''
    }
  }

  componentDidMount() {

    let accessToken = new URLSearchParams(window.location.search).get('access_token');
    if (!accessToken) return;

    fetch('https://api.spotify.com/v1/me', {
      headers: { 'Authorization': 'Bearer ' + accessToken }
    })
      .then(response => response.json())
      .then(data => {
        this.setState({
          user: {
            name: data.display_name
          }
        })
      })

    fetch(`https://api.spotify.com/v1/me/playlists?limit=50`, {
      headers: { 'Authorization': 'Bearer ' + accessToken }
    }).then(response => response.json())
      .then(playlistData => {
        let playlists = playlistData.items
        let trackDataPromises = playlists.map(playlist => {
          let responsePromise = fetch(playlist.tracks.href, {
            headers: { 'Authorization': 'Bearer ' + accessToken }
          })
          let trackDataPromise = responsePromise
            .then(response => response.json())
          return trackDataPromise
        })
        let allTracksDataPromises =
          Promise.all(trackDataPromises)
        let playlistsPromise = allTracksDataPromises.then(trackDatas => {
          trackDatas.forEach((trackData, i) => {
            playlists[i].trackDatas = trackData.items
              .map(item => item.track)
              .map(trackData => ({
                name: trackData.name,
                duration: trackData.duration_ms / 1000
              }))
          })
          return playlists
        })
        return playlistsPromise
      })
      .then(playlists => {
        this.setState({
          playlists: playlists.map(item => {
            return {
              name: item.name,
              songs: item.trackDatas.slice(0, 3),
              imageUrl: item.images[0].url
            }
          }
          )
        })
      })
  }

  render() {
    let playlistsToRender =
      this.state.user &&
        this.state.playlists
        ? this.state.playlists.filter(playlist => {
          let matchesPlaylist = playlist.name.toLowerCase().includes(
            this.state.filterString.toLowerCase())
          let matchesSong = playlist.songs.find(song =>
            song.name.toLowerCase().includes(this.state.filterString.toLowerCase()))
          return matchesPlaylist || matchesSong
        }) : []
    return (
      <div className="App">
        {this.state.user ?
          <div>
            <h1 >
              {this.state.user.name}'s playlist
            </h1>
            <DownloadPlaylist playlists={this.state.playlists} />
            <PlaylistCounter playlists={playlistsToRender} />
            <HoursCounter playlists={playlistsToRender} />
            <Filter onTextChange={text => this.setState({ filterString: text })} />
            {playlistsToRender.map(playlist =>
              <Playlist playlist={playlist} />
            )}

          </div> : <button onClick={() => window.location = 'http://localhost:8888/login'}
            style={{ 'fontSize': '16px', 'padding': '16px 32px', 'backgroundColor': '#1CD156', 'marginTop': '64px' }}>Sign In with Spotify</button>
        }
      </div>
    );
  }
}

export default App;
