import React, { Component } from 'react';
import '../css/Playlist.css';



class TagManager extends Component {
  constructor(props) {
    super(props)
    this.state = {
      tag: ''
    }

    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  handleChange = (event) => {
    let tag = event.target.value
    this.setState({ tag })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    this.props.addTag(this.state.tag)
    event.target.value = ''
  }

  handleClearForm = () => {
    this.value = ''
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          add a tag here
          <input type="text" name="tagInput" className="tagInput" onChange={this.handleChange} />
        </label>
        <input type="submit" value="submit" className="tagInput" onSubmit={this.handleClearForm} />
      </form>
    );
  }
}

class Playlist extends Component {
  render() {
    let playlist = this.props.playlist;
    return (
      <div className="playlist">
        <img src={playlist.imageUrl} alt="playlist" />
        <h3>{playlist.name}</h3>
        <ul>
          {playlist.songs.slice(0, 3).map(song =>
            <li>{song.name}</li>
          )}
        </ul>
        <div className="buttons">
          <p className="cta">see playlist</p>
        </div>
        <TagManager addTag={this.props.addTag} index={this.props.index} />


      </div>

    );
  }
}

export default Playlist;
