export function updateScore(answers, remainingTime, timeToGuess) {
    let comboCoef
    if (answers.length > 1) {
        comboCoef = answers.reverse().map((answer, index) => {
            console.log(answers)
            if (answer.correct === answers[index + 1].correct === true) {
                comboCoef += 1
                console.log(comboCoef)
            }
            return comboCoef
        })
    } else {
        comboCoef = 0
    }

    let score = answers.slice(-1)[0].correct === true ? `1.${comboCoef}` * (2 - ((timeToGuess - remainingTime) / timeToGuess)) * 100 : 0
    answers.slice(-1)[0].score = score
    return answers
}

export function getTotalScore(answers) {
    let totalScore = answers.map(answer => totalScore += answer.score)
}


