import React, { Component } from 'react';



class PlaylistCardTop extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isSelected: false
        }
    }

    handleClick = () => {
        this.setState({ isSelected: true })
        this.props.selectPlaylist(this.props.playlist)
    }

    render() {
        let playlist = this.props.playlist;
        return (
            <div className={this.state.isSelected ? "playlist selected" : "playlist"}
                onClick={this.handleClick} >
                <img src={playlist.imageUrl} alt="playlistImage" />
                <h3>{playlist.name}</h3>
                <ul>
                    {playlist.songs.slice(0, 3).map((song, key) =>
                        <li key={key}>{song.name}</li>
                    )}
                </ul>
                {this.props.children}
            </div>
        )
    }

}

export default PlaylistCardTop
