import React, { Component } from 'react';
import Button from '../UI-components/Button';
import Blindtest from './Blindtest'
// import SpotifyWebApi from 'spotify-web-api-js';

import '../../css/Blindtest.css';

// const spotifyApi = new SpotifyWebApi();



class BlindtestGame extends Component {
    constructor() {
        super()
        this.state = {
            nextSongTimer: 30
        }
    }

    render() {
        return (
            <div className="blindestGame">
                <div className="close">
                    <Button className='close' content="close" onClick={this.finishBlindtest} />
                </div>
                <div className="informations">
                    <p>You currently have 0 points</p>
                    <p>Previously played songs</p>
                </div>
                <div className="game">
                    <p>Next song in 5 seconds, get ready</p>
                    <p>You have {this.state.nextSongTimer} to guess the song</p>
                    <Blindtest access_token={this.props.access_token} />
                </div>

            </div>
        );
    }
}

export default BlindtestGame;
