import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

let defaultTextColor = "red";
let defaultStyle = {
  color: defaultTextColor
}

let fakeServerData = {
  user: {
    name: "Patrick",
    playlists: [
      {
        name: 'my fav',
        songs: [
          { name: 'get up stand up', duration: 1345 },
          { name: 'jammin', duration: 1564 }
        ]

      },
      {
        name: 'au clame',
        songs: [
          { name: 'imagine', duration: 2345 },
          { name: 'Hey Jude', duration: 1164 }
        ]
      },
      {
        name: 'cartes',
        songs: [
          { name: 'oooo', duration: 4345 },
          { name: 'loool', duration: 1924 }
        ]
      },
      {
        name: 'hip hop fr',
        songs: [
          { name: 'tournez manage', duration: 4345 },
          { name: 'allé l om', duration: 1924 }
        ]
      }
    ]
  }
}

class PlaylistCounter extends Component {
  render() {
    return (
      <div style={{ ...defaultStyle, width: '40%', display: 'inline-block' }}>
        <h2 style={defaultStyle}>
          {this.props.playlists.length} playlists
          </h2>
      </div >
    );
  }
}

class HoursCounter extends Component {
  render() {
    let allSongs = this.props.playlists;
    let totalDuration = 11;
    return (
      <div style={{ ...defaultStyle, width: '40%', display: 'inline-block' }}>
        <h2 style={defaultStyle}>
          {totalDuration} Hours
        </h2>
      </div >
    );
  }
}

class Filter extends Component {
  render() {
    return (
      <div style={defaultStyle}>
        <img src="#" alt="#" />
        <input type="text" />
      </div>
    );
  }
}

class Playlist extends Component {
  render() {
    return (
      <div style={{ ...defaultStyle, width: '25%', display: 'inline-block' }}>
        <img src="" alt="" />
        <h3>Playlist name</h3>
        <ul>
          <li>Song 1</li>
          <li>Song 2</li>
          <li>Song 2</li>
        </ul>
      </div>

    );
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = { serverData: {} }
  }

  componentDidMount() {
    setTimeout(() => {
      this.setState({ serverData: fakeServerData });
    }, 500);

  }

  render() {
    return (
      <div className="App">
        {this.state.serverData.user ?
          <div>

            <h1 >
              {this.state.serverData.user.name}'s playlist
            </h1>
            <PlaylistCounter playlists={this.state.serverData.user.playlists} />
            <HoursCounter />
            <Filter />
            <Playlist />
            <Playlist />
            <Playlist />
            <Playlist />
          </div> : <h1>loading</h1>
        }
      </div>
    );
  }
}

export default App;
