import React, { Component } from 'react';
import Button from './UI-components/Button';
import SpotifyWebApi from 'spotify-web-api-js';

const spotifyApi = new SpotifyWebApi();



class Blindtest extends Component {
    constructor() {
        super();
        this.state = {
            playlist: {}
        }
    }

    playBlindtest = (event) => {
        let blindtestGuess = event.currentTarget.value
        let currentSong = spotifyApi.getMyCurrentPlaybackState()
            .then(response => {
                return response.item.name
            })
        console.log(currentSong)
    }


    render() {
        return (
            <div className="blindest">
                <p>Try to guess the song now playing</p>
                <input className="answer-input" type="text" onKeyUp=
                    {this.playBlindtest} />
            </div>
        );
    }
}

export default Blindtest;
