import React, { Component } from 'react';
import SpotifyWebApi from 'spotify-web-api-js';

const spotifyApi = new SpotifyWebApi();

class AristSelector extends Component {
    constructor(props) {
        super(props);

        this.state = {
            relatedArtists: [],
        }
    }

    getArtistImage = (artistId) => {
        spotifyApi.getArtist(artistId).then(response => {
            console.log(response)
            this.setState({
                currentArtist: {
                    imageUrl: ""
                }
            })
        }
        )
    }

    getRelatedArtist = (artistId) => {
        this.setState({ relatedArtists: [] })
        spotifyApi.getArtistRelatedArtists(artistId)
            .then(response => {
                response.artists.map(artist => {
                    let imageUrl = artist.images[1] ? artist.images[1].url : ""
                    let relatedArtists = [...this.state.relatedArtists, {
                        name: artist.name,
                        imageUrl,
                        popularity: artist.popularity
                    }]
                    return this.setState({ relatedArtists })
                })
            })
    }

    componentDidUpdate(prevProps) {
        if (prevProps.currentData !== this.props.currentData) {
            this.getRelatedArtist(this.props.currentData.artist.id)
        }
    }

    componentDidMount() {
        console.log(this.props)
        this.getRelatedArtist(this.props.currentData.artist.id)
    }

    render() {
        // let currentArtist = this.props.currentData.artist.name
        return (
            <div>
                <p>L'artiste est ...</p>
                <ul>
                    {/* <li>
                        <img
                            src={artist.imageUrl}
                            alt={artist.name}
                            className="artist-avatar" />
                        {artist.name}

                    </li> */}

                    {this.state.relatedArtists.slice(0, 3).map((artist, key) =>
                        <li key={key}>
                            <img
                                src={artist.imageUrl}
                                alt={artist.name}
                                className="artist-avatar" />
                            {artist.name}
                        </li>
                    )}
                </ul>
            </div>
        )
    }
}

export default AristSelector;
