import React, { Component } from 'react';
import Button from './UI-components/Button';
import SpotifyWebApi from 'spotify-web-api-js';
import '../css/App.css';

const spotifyApi = new SpotifyWebApi();



// js 
// import { fetchPlaylistInfos, fetchPlaylistData } from '../api/fetchData'

class LoginPage extends Component {
    constructor() {
        super();
        const params = this.getHashParams();
        const access_token = params.access_token;
        if (access_token) {
            spotifyApi.setAccessToken(access_token)
        }
        this.state = {
            loggedIn: access_token ? true : false,
            nowPlaying: { name: 'Unknown yet', albumArt: '' }
        }
    }

    getHashParams() {
        var hashParams = {};
        var e, r = /([^&;=]+)=?([^&;]*)/g,
            q = window.location.hash.substring(1);
        e = r.exec(q)
        while (e) {
            hashParams[e[1]] = decodeURIComponent(e[2]);
            e = r.exec(q);
        }
        return hashParams;
    }

    getNowPlaying() {
        spotifyApi.getMyCurrentPlaybackState()
            .then(response => {
                console.log(response)
            })
    }

    getUserInfo() {
        spotifyApi.getPlaylist()
            .then(response => {
                console.log(response)
            })
    }

    // getNowPlaying() {
    //     spotifyApi.getMyCurrentPlaybackState()
    //         .then((response) => {
    //             this.setState({
    //                 nowPlaying: {
    //                     name: response.item.name,
    //                     albumArt: response.item.album.images[0].url
    //                 }
    //             });
    //         })
    // }

    render() {
        return (
            <div>
                <p style={{ margin: 0 }}>You currently have no playlist loaded. Click the button
                    below to login to your spotify account
                </p>
                <a href="http://localhost:8888">
                    <Button content="Sign In with Spotify" />
                </a>
                <div>
                    Now Playing: {this.state.nowPlaying.name}
                </div>
                <img src={this.state.nowPlaying.albumArt} alt="" style={{ height: 150 }} />
                {this.state.loggedIn &&
                    <button onClick={() => this.getNowPlaying()}>
                        Check Now Playing
                    </button>
                }

            </div>

        );
    }
}

export default LoginPage;
