import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

let defaultTextColor = "red";
let defaultStyle = {
  color: defaultTextColor
}

let fakeServerData = {
  user: {
    name: "Patrick",
    playlists: [
      {
        name: 'my fav',
        songs: ["get up stand up", "Jammin"]
      },
      {
        name: 'au clame',
        songs: ["imagine", "Hey Jude"]
      },
      {
        name: 'cartes',
        songs: ["Lolilol", "crotte"]
      },
      {
        name: 'hip hop fr',
        songs: ["tournez les violong", "c'est bien"]
      }
    ]
  }
}

class Aggregate extends Component {
  render() {
    return (
      <div style={{ ...defaultStyle, width: '40%', display: 'inline-block' }}>
        <h2 style={defaultStyle}>Number Text</h2>
      </div >
    );
  }
}

class Filter extends Component {
  render() {
    return (
      <div style={defaultStyle}>
        <img src="#" alt="#" />
        <input type="text" />
      </div>
    );
  }
}

class Playlist extends Component {
  render() {
    return (
      <div style={{ ...defaultStyle, width: '25%', display: 'inline-block' }}>
        <img src="" alt="" />
        <h3>Playlist name</h3>
        <ul>
          <li>Song 1</li>
          <li>Song 2</li>
          <li>Song 2</li>
        </ul>
      </div>

    );
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = { serverData: {} }
  }

  componentDidMount() {
    this.setState({ serverData: fakeServerData });
  }

  render() {
    return (
      <div className="App">
        <h1 >
          {this.state.serverData.user && this.state.serverData.user.name}'s playlist
        </h1>
        <Aggregate playlists={this.state.serverData.user && this.state.serverData.user.playlists} />
        <Aggregate />
        <Filter />
        <Playlist />
        <Playlist />
        <Playlist />
        <Playlist />
      </div>
    );
  }
}

export default App;
