import React, { Component } from 'react';
import Button from './UI-components/Button';
import SpotifyWebApi from 'spotify-web-api-js';
import '../css/App.css';

const spotifyApi = new SpotifyWebApi();



// js 
// import { fetchPlaylistInfos, fetchPlaylistData } from '../api/fetchData'

class LoginPage extends Component {
    constructor() {
        super();
        const access_token = this.props.access_token
        if (access_token) {
            spotifyApi.setAccessToken(access_token)
        }
        this.state = {
            nowPlaying: { name: 'Unknown yet', albumArt: '' }
        }
    }

    getNowPlaying() {
        spotifyApi.getMyCurrentPlaybackState()
            .then(response => {
                console.log(response)
            })
    }

    render() {
        return (
            <div>
                <p style={{ margin: 0 }}>You currently have no playlist loaded. Click the button
                    below to login to your spotify account
                </p>
                <a href="http://localhost:8888">
                    <Button content="Sign In with Spotify" />
                </a>


            </div>

        );
    }
}

export default LoginPage;
