import React, { Component } from 'react';
import PlaylistCardTop from '../PlaylistCardTop';

import SpotifyWebApi from 'spotify-web-api-js';

const spotifyApi = new SpotifyWebApi();





class PlaylistPickerCards extends Component {
    constructor(props) {
        super(props);
        this.state = {
        }
    }

    handleClick = () => {
        this.setState({ isSelected: !this.state.isSelected })
        this.props.selectPlaylist(this.props.playlist)
    }

    componentDidMount() {

        spotifyApi.setAccessToken(this.props.access_token)
        // let access_token = this.props.access_token
        // spotifyApi.getTrack("4TCAjIbfyYFr7wkiIXRYYY").then(response => console.log(response))
        // fetch('https://api.spotify.com/v1/tracks/4TCAjIbfyYFr7wkiIXRYYY', {
        //     headers: { 'Authorization': 'Bearer ' + access_token }
        // }).then(response => console.log(response))

        this.props.playlist.songs.map(song => {
            let trackId = song.uri.match(/[^:]*$/)
            spotifyApi.getTrack(trackId[0])
                .then(trackResponse => {
                    console.log(typeof trackResponse.artists[0].id)
                    // spotifyApi.getArtist(trackResponse.artists[0].id)
                    //     .then(artistResponse => console.log(artistResponse))
                })
        })
    }

    render() {
        let playlist = this.props.playlist;
        let isSelected = this.props.selectedPlaylist === playlist ? true : false
        return (
            <div
                onClick={this.handleClick}
                className={isSelected ? "playlist selected" : "playlist"}
            >
                <PlaylistCardTop
                    playlist={playlist}
                    access_token={this.props.access_token}
                />
            </div>

        )
    }

}

export default PlaylistPickerCards
